sys_script_include.script:
var CustomerFeedbackSentiment = Class.create();
CustomerFeedbackSentiment.prototype = {
    initialize: function() {},

    analyzeSentiment: function(feedbackText) {
        var requestBody = JSON.stringify({
            document: {
                type: "PLAIN_TEXT",
                content: feedbackText
            }
        });

        var restMessage = new sn_ws.RESTMessageV2('Google Sentiment Analysis', 'POST');
        restMessage.setRequestBody(requestBody);

        var response;
        try {
            response = restMessage.execute();
            var responseBody = response.getBody();
            var parsedResponse = JSON.parse(responseBody);

            var sentiment = {
                score: parsedResponse.documentSentiment.score,
                magnitude: parsedResponse.documentSentiment.magnitude
            };

            return sentiment;

        } catch (error) {
            gs.error("Error in CustomerFeedbackSentiment: " + error.message);
            return null;
        }
    },

    type: 'CustomerFeedbackSentiment'
};
